mapper.select:
select (select ifnull(COUNT(*),0) from sys_login_detail where 1=1
and date_format(create_date,'%Y-%m-%d') >= date_format(#{request.beginDate},'%Y-%m-%d')
and date_format(create_date,'%Y-%m-%d') <= date_format(#{request.endDate},'%Y-%m-%d')
) as visitorsTotalNumber, (SELECT ifnull(a.count,0) FROM (select count(distinct ip) as count from sys_login_detail where 1=1
and to_days(create_date) = to_days(now())
and date_format(create_date,'%Y-%m-%d') >= date_format(#{request.beginDate},'%Y-%m-%d')
and date_format(create_date,'%Y-%m-%d') <= date_format(#{request.endDate},'%Y-%m-%d')
) a ) newVisitors, (select ifnull(COUNT(*),0) from sys_login_detail where temporary_user = '1'
and date_format(create_date,'%Y-%m-%d') >= date_format(#{request.beginDate},'%Y-%m-%d')
and date_format(create_date,'%Y-%m-%d') <= date_format(#{request.endDate},'%Y-%m-%d')
)as totalLogins, (select ifnull(count(*),0) from sys_user where 1=1
and to_days(create_date) = to_days(now())
and date_format(create_date,'%Y-%m-%d') >= date_format(#{request.beginDate},'%Y-%m-%d')
and date_format(create_date,'%Y-%m-%d') <= date_format(#{request.endDate},'%Y-%m-%d')
)as newRegistration, (select ifnull(COUNT(*),0) from sys_user_account where type = '2'
and date_format(create_date,'%Y-%m-%d') >= date_format(#{request.beginDate},'%Y-%m-%d')
and date_format(create_date,'%Y-%m-%d') <= date_format(#{request.endDate},'%Y-%m-%d')
)as envelopesNumber, (select ifnull(SUM(amount),0) from sys_user_account where type = '2' and spending = '1'
and date_format(create_date,'%Y-%m-%d') >= date_format(#{request.beginDate},'%Y-%m-%d')
and date_format(create_date,'%Y-%m-%d') <= date_format(#{request.endDate},'%Y-%m-%d')
)as envelopesTotalAmount, (select ifnull(COUNT(*),0) from sys_user_account where type = '1'
and date_format(create_date,'%Y-%m-%d') >= date_format(#{request.beginDate},'%Y-%m-%d')
and date_format(create_date,'%Y-%m-%d') <= date_format(#{request.endDate},'%Y-%m-%d')
)as envelopesRemoveNumber, (select IFNULL(SUM(amount),0) from sys_user_account where type = '1' and spending = '0'
and date_format(create_date,'%Y-%m-%d') >= date_format(#{request.beginDate},'%Y-%m-%d')
and date_format(create_date,'%Y-%m-%d') <= date_format(#{request.endDate},'%Y-%m-%d')
)as totalPrize, (select IFNULL(SUM(amount),0) from recharge_detail where pay_status = '2'
and date_format(create_date,'%Y-%m-%d') >= date_format(#{request.beginDate},'%Y-%m-%d')
and date_format(create_date,'%Y-%m-%d') <= date_format(#{request.endDate},'%Y-%m-%d')
)as totalRecharge, (select IFNULL(SUM(amount),0) from sys_user_account where type = '3' and spending = '1' and audit = '3'
and date_format(create_date,'%Y-%m-%d') >= date_format(#{request.beginDate},'%Y-%m-%d')
and date_format(create_date,'%Y-%m-%d') <= date_format(#{request.endDate},'%Y-%m-%d')
)as totalWithdrawal

select id
from sys_user
where create_date between #{begin} and #{end}

select distinct create_by
from sys_login_detail
where create_date between #{begin} and #{end}
