mapper.select:
select c.id as userId, b.id as redPacketsId, b.name as awards, a.amount as amount, a.create_date as createDate from unpack_red_packets_detail a inner join unpack_red_packets b on a.unpack_red_packets_id = b.id inner join sys_user c on a.create_by = c.id where 1 = 1
and c.id = #{request.keyWord}
or c.account = #{request.keyWord}
or c.phone = #{request.keyWord}
and date_format(a.create_date, '%Y-%m-%d') >= date_format(#{request.beginDate}, '%Y-%m-%d')
and date_format(a.create_date, '%Y-%m-%d') <= date_format(#{request.endDate}, '%Y-%m-%d')
